import React, { Component } from 'react';
import axios from 'axios';
import { MDBContainer, MDBCol, MDBRow, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBRotatingCard,
    MDBIcon, MDBBtn } from "mdbreact";


export default class ListTous extends Component {

    state = {
        flipped2: false
      }

      handleFlipping = id => () => {
        const cardId = `flipped${id}`;
        this.setState({ [cardId]: !this.state[cardId] });
      }


    constructor(props) {
        super(props);
        this.state = { profil: [] };

    }
    componentDidMount() {
        axios.get('http://localhost:8080/produit')
            .then(response => {
                console.log('i am a response', response)
                this.setState({ profil: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    liste() {
        return <div>
            <div>


                <table className="table table-striped w-auto">
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Titre</th>
                            <th>Description</th>
                            <th>Image de produit</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            (this.state.profil.length > 0) ? (this.state.profil.map((obj) => {
                                return <tr className="table-info" key={obj._id}>
                                    <td>{obj._id}</td>
                                    <td>{obj.titre}</td>
                                    <td>{obj.description}</td>
                                    <td><img width="150px" height="50px" src={'http://localhost:8080/user/'+obj.photo_produit} alt="ima" />
                                    </td>
                                    {console.log(obj)}
                                </tr>

                            })) : ('')
                        }
                    </tbody>
                </table>
            </div>

            <MDBContainer>
      <MDBRow between>
        <MDBCol style={{ minHeight: '26rem', maxWidth: "22rem" }}>

            <MDBCard className="face front">
              <MDBCardImage top src="https://mdbootstrap.com/img/Photos/Others/photo5.jpg" overlay="white-slight" hover
                waves alt="MDBCard image cap" />
              <MDBCardBody className="text-center">
                <MDBCardTitle tag="h4">MDBCard Title
                  <MDBIcon icon="share-alt text-dark float-right" onClick={this.handleFlipping(2)} />
                </MDBCardTitle>
                <MDBCardText>
                  Some quick example text to build on the card title and make
                  up the bulk of the card&apos;s content.
                </MDBCardText>
                <a href="#!" className="black-text">
                  <h6 onClick={this.handleFlipping}>
                    Read more
                    <MDBIcon icon="angle-double-right" />
                  </h6>
                </a>
              </MDBCardBody>
            </MDBCard>

        </MDBCol>
      </MDBRow>
    </MDBContainer>

        </div>
    }
    render() {
        return (
            <div>
                {this.liste()}
            </div>
        );
    }
}
