import React, { Component } from "react";
import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavLink, MDBNavbarToggler, MDBCollapse, MDBFormInline } from "mdbreact";
import { MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter, MDBRow, MDBInput } from "mdbreact";
import { Redirect } from 'react-router-dom';
const axios = require('axios');
class SideNavPage extends Component {

  state = {
    modal1: false,
    modal2: false,
    modal3: false,
    modal4: false,
    modal5: false,
    collapseID: "",
  }

  constructor(props) {

    super(props);

    this.state = {
      nomUtilisateur: '',
      password:'',
      email:'',
      redirect: ''
    };
    this.onChange = this.onChange.bind(this)
  }

  componentDidMount() {
    this.setState({ redirect: localStorage.getItem('Profil') })
}

// setRedirect = () => {
//     this.setState({
//         redirect: localStorage.getItem('Profil')
//     })
// }

// renderinscrit = () => {
//         return <Redirect to='/' />
// }

  onChange(event) {
    this.setState({
        [event.target.name]: event.target.value
    })
}

register() {
  axios.post('http://localhost:8080/register', {
    nomUtilisateur: this.state.nomUtilisateur,
    email: this.state.email,
    password: this.state.password
})
    .then(function (response) {
        console.log(response.data);
        localStorage.setItem('Profil', response.data.nomUtilisateur);
       console.log(localStorage.getItem('Profil'));
    })
    .catch(function (error) {
        console.log(error);
    });
}


//popops login fonction 
  toggleCollapse = collapseID => () =>
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));



    toggle = nr => () =>  {
      let modalNumber = "modal" + nr;
      this.setState({
        [modalNumber]: !this.state[modalNumber]
      });
    }


  render() {
    return (
      <div>
        {this.renderinscrit()}
        <MDBNavbar color="red" dark expand="md" style={{ marginTop: "20px" }} id="navbar">
          <MDBNavbarBrand>
            <strong className="white-text">E-COM</strong>
          </MDBNavbarBrand>
          <MDBNavbarToggler onClick={this.toggleCollapse("navbarCollapse3")} />
          <MDBCollapse id="navbarCollapse3" isOpen={this.state.collapseID} navbar>
            <MDBNavbarNav left>
              <MDBNavItem >
                <MDBNavLink to="#!" className="nav-header">A propos</MDBNavLink>
              </MDBNavItem>
              <MDBNavItem>
                <MDBNavLink to="#!" className="nav-header">Produits</MDBNavLink>
              </MDBNavItem>
              <MDBNavItem>
                <MDBNavLink to="#!" className="nav-header">Contacts</MDBNavLink>
              </MDBNavItem>

            </MDBNavbarNav>
            <MDBNavbarNav right>
              <MDBNavItem>
                <MDBNavLink to="#!" className="nav-header" rounded onClick={this.toggle(1)}>Connexion</MDBNavLink>
              </MDBNavItem>
              <MDBNavItem>
                <MDBNavLink to="#!" className="nav-header" rounded onClick={this.toggle(2)}>Inscription</MDBNavLink>
              </MDBNavItem>
              <MDBNavItem>
                <MDBFormInline waves>
                  <div className="md-form my-0">
                    <input className="form-control mr-sm-2" type="text" placeholder="Recherche produit" aria-label="Recherche produit" />
                  </div>
                </MDBFormInline>
              </MDBNavItem>
            </MDBNavbarNav>
          </MDBCollapse>
        </MDBNavbar>


        <MDBRow>
        <form className="mx-3 grey-text" onSubmit={e => {
                            e.preventDefault()
                            // this.setRedirect() 
                            this.register()
                            }} >
          <MDBModal isOpen={this.state.modal2} toggle={this.toggle(2)}>
            <MDBModalHeader
              className="text-center"
              titleClass="w-100 font-weight-bold"
              toggle={this.toggle(2)}
            >
              Inscription
            </MDBModalHeader>
            <MDBModalBody>
                <MDBInput
                  label="Votre nom"
                  icon="user"
                  group
                  type="text"
                  validate
                  error="wrong"
                  success="right" value={this.state.nomUtilisateur}  onChange={this.onChange} name="nomUtilisateur"
                />
                <MDBInput
                  label="Votre e-mail"
                  icon="envelope"
                  group
                  type="email"
                  validate
                  error="wrong"
                  success="right"  value={this.state.email}  onChange={this.onChange} name="email"
                />
                <MDBInput
                  label="Votre mot de passe"
                  icon="lock"
                  group
                  type="password"
                  validate   value={this.state.password}  onChange={this.onChange} name="password"
                />
            </MDBModalBody>
            <MDBModalFooter className="justify-content-center">
              <button  className="btn btn-primary" onClick={this.toggle(2)}>S'inscrire</button>
            </MDBModalFooter>
          </MDBModal>
          </form>
        </MDBRow>

        <MDBRow>
          <MDBModal isOpen={this.state.modal1} toggle={this.toggle(1)}>
            <MDBModalHeader
              className="text-center"
              titleClass="w-100 font-weight-bold"
              toggle={this.toggle(1)}
            >
              Connexion
            </MDBModalHeader>
            <MDBModalBody>
              <form className="mx-3 grey-text">
                <MDBInput
                  label="taper votre e-mail"
                  icon="envelope"
                  group
                  type="email"
                  validate
                  error="wrong"
                  success="right"
                />
                <MDBInput
                  label="taper votre mot de passe"
                  icon="lock"
                  group
                  type="password"
                  validate
                />
              </form>
            </MDBModalBody>
            <MDBModalFooter className="justify-content-center">
              <MDBBtn color="deep-orange" onClick={this.toggle(1)}>Connecter</MDBBtn>
            </MDBModalFooter>
          </MDBModal>
        </MDBRow>


      </div>
    );
  }
}

export default SideNavPage;
