import React from 'react';
import axios from 'axios';

class PostFrontToBack extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
     nom: '',
     email:'',
      password:'',
      photo_profil:'',
      profil: []
    };
    this.onChange = this.onChange.bind(this)
    this.handleUploadImage = this.handleUploadImage.bind(this);
  }




  onChange(event) {
    this.setState({
        [event.target.name]: event.target.value
    })
}


componentDidMount() {
  axios.get('http://localhost:8080')
      .then(response => {
          console.log('i am a response', response)
          this.setState({ profil: response.data });
      })
      .catch(function (error) {
          console.log(error);
      })

}


  handleUploadImage(ev) {
    ev.preventDefault();

    const data = new FormData();
    data.append('photo_profil', this.uploadInput.files[0]);
    data.append('nom',this.state.nom);
    data.append('email',this.state.email);
    data.append('password',this.state.password)

    fetch('http://localhost:8080/profil', {
      method: 'POST',
      body: data,
    }).then((response) => {
      response.json().then((body) => {
        this.setState({ photo_profil: `http://localhost:8080/profil/${body.photo_profil}` });
        console.log('ity ilay body.fil',body.photo_profil);

      });
    });
  }


  liste() {
    return <div>
        <div>
            <table className="table table-striped w-auto">
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Nom</th>
                        <th>Email</th>
                        <th>Mot de passe</th>
                        <th>Photo de profil</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        (this.profil.length > 0) ? (this.state.profil.map((obj) => {
                            return <tr className="table-info" key={obj._id}>
                                <td>{obj._id}</td>
                                <td>{obj.nom}</td>
                                <td>{obj.email}</td>
                                <td>{obj.password}</td>
                                <td><img width="150px" height="50px" src={'http://localhost:8080/user/'+obj.photo_profil} alt="ima" />
                                </td>
                                {console.log(obj)}
                            </tr>

                        })) : ('')
                    }
                </tbody>
            </table>
        </div>

    </div>
}

  render() {
    return (
      <div>
      <form onSubmit={this.handleUploadImage}>
        <label>Nom:</label>
        <input type="text"
          value={this.state.value}
          onChange={this.onChange}
          name="nom" /><br></br>
          <label>email:</label>
        <input type="email"
          value={this.state.value}
          onChange={this.onChange}
          name="email" /><br></br>
        <label>passeword:</label>
        <input type="password"
          value={this.state.value}
          onChange={this.onChange}
          name="password" /><br></br>       

          <input ref={(ref) => { this.uploadInput = ref; }} type="file" name="photo_profil"/>

          <button>Ajouter</button>
      </form>
      {this.liste()}
      </div>
    );
  }
}

export default PostFrontToBack;
