import React, { Component } from 'react';
import axios from 'axios';
import { MDBContainer, MDBCol, MDBRow, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBRotatingCard,
    MDBIcon} from "mdbreact";



export default class ListTous extends Component {



    constructor(props) {
        super(props);
        this.state = { profil: [] };

    }
    componentDidMount() {
        axios.get('http://localhost:8080/produit')
            .then(response => {
                console.log('i am a response', response)
                this.setState({ profil: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    liste() {
        return <div className="container-fluid">


            <MDBContainer>
                <MDBRow between>
                    <MDBCol style={{ minHeight: '26rem', maxWidth: "22rem" }}>

                        <MDBCard className="face front">
                        <MDBCardImage top src="https://mdbootstrap.com/img/Photos/Others/photo5.jpg" overlay="white-slight" hover
                            waves alt="MDBCard image cap" />
                        <MDBCardBody className="text-center">
                            <MDBCardTitle tag="h4">MDBCard Title
                            <MDBIcon icon="share-alt text-dark float-right" onClick={this.handleFlipping(2)} />
                            </MDBCardTitle>
                            <MDBCardText>
                            Some quick example text to build on the card title and make
                            up the bulk of the card&apos;s content.
                            </MDBCardText>
                            <a href="#!" className="black-text">
                            <h6 onClick={this.handleFlipping}>
                                Read more
                                <MDBIcon icon="angle-double-right" />
                            </h6>
                            </a>
                        </MDBCardBody>
                        </MDBCard>

                    </MDBCol>
                </MDBRow>
            </MDBContainer>

            <section className="my-5">

                <div className="row view-group" id="colonne">
                    {this.state.profil.length > 0 ? (

                        this.state.profil.map(user => (

                            <div class="item col-xs-4 col-lg-4">

                                <div className="card card-cascade narrower card-ecommerce">

                                    <div class="view view-cascade overlay">
                                        <img width="150px" height="50px" src={'http://localhost:8080/user/' + user.photo_produit} alt="ima" />
                                    </div>

                                    <div className="card-body card-body-cascade">

                                        <h6 id="description">Nom Produit  &nbsp;<span id="nomproduit">{user.titre}</span></h6>

                                        <p className="card-text"><strong><span id="description">Description</span></strong>&nbsp;&nbsp; <div id="point">{user.description}</div> </p>
                                        <span className="spanprix">
                                            <strong>Prix: </strong>
                                        </span><br />

                                        <span class="float-right">

                                            <span class="float-right">
                                                <a data-toggle="tooltip" data-placement="top" title="Add to Cart">
                                                    <i class="fas fa-shopping-cart grey-text ml-3"></i>
                                                </a>
                                                <a data-toggle="tooltip" data-placement="top" title="J'adore">
                                                </a>
                                                <a data-toggle="tooltip" data-placement="top" title="J'adore">
                                                    <i class="fas fa-share-alt grey-text ml-3"></i>
                                                </a>

                                            </span>

                                        </span>

                                        <div className="card-footer px-1">
                                        </div>
                                    </div>

                                </div>
                            </div>

                        ))
                    ) : (
                            <div></div>
                        )}
                </div>
            </section>







        </div>
    }
    render() {
        return (
            <div>
                {this.liste()}
            </div>
        );
    }
}
