import React from 'react';
import { MDBRow,MDBInput, MDBBtn,MDBCard, MDBCardBody} from "mdbreact";

class PostFrontToBack extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      titre: '',
      description:'',
      photo_produit:'',
      prix: ''

    };

    this.onChange = this.onChange.bind(this)
    this.handleUploadImage = this.handleUploadImage.bind(this);
  }
  onChange(event) {
    this.setState({
        [event.target.name]: event.target.value
    })
}
  handleUploadImage(ev) {
    ev.preventDefault();

    const data = new FormData();
    data.append('photo_produit', this.uploadInput.files[0]);
    data.append('titre',this.state.titre);
    data.append('description',this.state.description);
    data.append('prix',this.state.prix);

    fetch('http://localhost:8080/produit', {
      method: 'POST',
      body: data,
    }).then((response) => {
      response.json().then((body) => {
        this.setState({ photo_produit: `http://localhost:8080/produit/${body.photo_produit}` });
        console.log('ity ilay body.fil',body.photo_produit);

      });
    });
  }

  render() {
    return (

        <div>

      <form onSubmit={this.handleUploadImage}>
        <label>Titre:</label>
        <input type="text" value={this.state.value}  onChange={this.onChange} name="titre" /><br></br>
        <label>Description:</label>
        <input type="text" value={this.state.value} onChange={this.onChange}  name="description" /><br></br>
        <label>Prix :</label>
        <input type="text" value={this.state.value} onChange={this.onChange}  name="prix" /><br></br>
        <label>Images de votre produit : </label>  
          <input ref={(ref) => { this.uploadInput = ref; }} type="file" name="photo_produit"/><br/>

          <button className="btn btn-primary">Ajouter votre produit</button>
      </form>
            <MDBCard width="50%">
              <MDBCardBody>
                <form>
                  <p className="h4 text-center py-4">Sign up</p>
                  <div className="grey-text">
                    <MDBInput
                      label="Your name"
                      icon="user"
                      group
                      type="text"
                      validate
                      error="wrong"
                      success="right"
                    />
                    <MDBInput
                      label="Your email"
                      icon="envelope"
                      group
                      type="email"
                      validate
                      error="wrong"
                      success="right"
                    />
                    <MDBInput
                      label="Confirm your email"
                      icon="exclamation-triangle"
                      group
                      type="text"
                      validate
                      error="wrong"
                      success="right"
                    />
                    <MDBInput
                      label="Your password"
                      icon="lock"
                      group
                      type="password"
                      validate
                    />
                  </div>
                  <div className="text-center py-4 mt-3">
                    <MDBBtn color="cyan" type="submit">
                      Register
                    </MDBBtn>
                  </div>
                </form>
              </MDBCardBody>
            </MDBCard>
      </div>
    );
  }
}

export default PostFrontToBack;
