import React, { Component } from "react";
import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavLink, MDBNavbarToggler, MDBCollapse, MDBFormInline } from "mdbreact";
import { MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter, MDBRow, MDBInput } from "mdbreact";
import { Redirect } from 'react-router-dom';
const axios = require('axios');
class SideNavPage extends Component {

  state = {
    modal1: false,
    modal2: false,
    modal3: false,
    modal4: false,
    modal5: false,
    collapseID: "",
  }

  constructor(props) {

    super(props);

    this.state = {
      nomUtilisateur: '',
      password: '',
      email: '',
    };
    this.onChange = this.onChange.bind(this)
  }

  onChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  renderRedirect = () => {
    if (localStorage.getItem('nom') == 'true') {
      return <Redirect to='/admin' />
    }
    else {
      return <Redirect to='/'/>
    }
  }

  conn = () => {
    localStorage.setItem('nom', 'true')
  }


  register() {
    axios.post('http://localhost:8080/register', {
      nomUtilisateur: this.state.nomUtilisateur,
      email: this.state.email,
      password: this.state.password
    })
      .then(function (response) {
        console.log('response', response.data.nomUtilisateur);
        localStorage.setItem('nom', 'true')
        console.log('local STORAGE :', localStorage.getItem('nom'));

      })
      .catch(function (error) {
        console.log(error);
      });
  }

  login() {
    axios.post('http://localhost:8080/login', {
      nomUtilisateur: this.state.nomUtilisateur,
      password: this.state.password
    })
      .then(function (response) {

          console.log('data: ',this.state.nomUtilisateur);

        if (response.data) {

          console.log('test');

          localStorage.setItem('nom', 'true')
        } else {
          console.log('else io e');

          localStorage.setItem('nom', 'false')
        }

        console.log('response', response.data);

        //  console.log('local STORAGE :',localStorage.getItem('nom'));

      })
      .catch(function (error) {
        console.log(error);
      });
  }


  //popops login fonction 
  toggleCollapse = collapseID => () =>
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));
  toggle = nr => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber]
    });
  }


  state={
    collapseID: ""
  }

  toggleCollapse = collapseID => () =>
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));

  closeCollapse = collapseID => () =>
    this.state.collapseID === collapseID && this.setState({ collapseID: "" });



  render() {
    const overlay = (
      <div
        id="sidenav-overlay"
        style={{ backgroundColor: "transparent" }}
        onClick={this.toggleCollapse("mainNavbarCollapse")}
      />
    );
    return (
      <div className="flyout">
        {this.renderRedirect()}
        <MDBNavbar color="red" dark expand="md" style={{ marginTop: "1px" }} id="navbar" fixed="top">
          <MDBNavbarBrand>
            <strong className="white-text">E-COM</strong>
          </MDBNavbarBrand>
          <MDBNavbarToggler onClick={this.toggleCollapse("navbarCollapse3")} />
          <MDBCollapse id="navbarCollapse3" isOpen={this.state.collapseID} navbar>
            <MDBNavbarNav left>
              <MDBNavItem >
                <MDBNavLink to="" className="nav-header">A propos</MDBNavLink>
              </MDBNavItem>
              <MDBNavItem>
                <MDBNavLink to="" className="nav-header">Produits</MDBNavLink>
              </MDBNavItem>
              <MDBNavItem>
                <MDBNavLink to="" className="nav-header">Contacts</MDBNavLink>
              </MDBNavItem>

            </MDBNavbarNav>
            <MDBNavbarNav right>
              <MDBNavItem>
                <MDBNavLink to="" className="nav-header" rounded onClick={this.toggle(1)}>Connexion</MDBNavLink>
              </MDBNavItem>
              <MDBNavItem>
                <MDBNavLink to="" className="nav-header" rounded onClick={this.toggle(2)}>Inscription</MDBNavLink>
              </MDBNavItem>
